import { useEffect } from "react"

export default function Timer({dispatch, totalSeconds}) {
    console.log(totalSeconds);
    let minutes = Math.floor(totalSeconds/60);
    let seconds = (totalSeconds%60);
    useEffect(()=>{
        const myTimer = 
        setTimeout(() => {
          dispatch({type:"tick"});
        }, 1000);
        return(clearInterval(myTimer))
    },[])
    return(
        <div className="timer">
            {minutes}:{seconds}
        </div>  
    )
}
